google.setOnLoadCallback(function(){
  customSearchControl = new google.search.CustomSearchControl('012940815912449519308:-jyetlpxnyi');
  customSearchControl.setResultSetSize(google.search.Search.FILTERED_CSE_RESULTSET);  
}, true);
function googleDoSearch(modName) {
  if($('search-termPanel-'+modName+'-terms').value != _('search.text.entry.label')) {
    var drawOptions = new google.search.DrawOptions();
    drawOptions.setInput($('search-termPanel-'+modName+'-terms'));
    customSearchControl.draw('cse', drawOptions);
    customSearchControl.execute($('search-termPanel-'+modName+'-terms').value);
    $('cse').show();
  } else {
    $('cse').hide();
  }
}
Ext.onReady(function(){
  Curriki.module.EventManager.addListener('Curriki.module.search:doSearch', function(modName){
    googleDoSearch(modName);
  });
Curriki.module.EventManager.addListener('Curriki.module.search:tabchange', function(tabId){
    googleDoSearch(tabId);
  });
});

